mapper.select:
select * from (select result1.*, ROWNUM as rnum from (select num,writer,title,content,viewCount,regdate from board_review
order by num desc) result1)
where rnum >= #{startRowNum} and rnum <= #{endRowNum}

mapper.update:
update board_review
set viewCount=viewCount+1
where num=#{num}


update board_review
set title=#{title}, content=#{content}
where num=#{num}

update board_review
set likeCount = likeCount+1
where num=#{num}
