mapper.update:
UPDATE shop
SET remainCount=remainCount-1
WHERE num=#{num}

UPDATE shop
SET remainCount=remainCount+1
WHERE num=#{shopnum}

mapper.select:
SELECT price
FROM shop
WHERE num=#{num}

SELECT seatnum
from shop
where num=#{num}

SELECT location
FROM shop
where num=#{num}

mapper.update:
update shop
set name=#{name}, cateNum=#{cateNum} ,price=#{price}, image=#{image}, cast=#{cast},
	hours=#{hours}, location=#{location}
where num=#{num}
